mapper.select:
SELECT /* bookSelectMapper.findTotalCount */ COUNT(*) FROM BOOK
WHERE LOWER(TITLE) LIKE CONCAT(CONCAT('%', #{searchText}), '%') OR LOWER(AUTHOR) LIKE CONCAT(CONCAT('%', #{searchText}), '%')
WHERE CREATED_BY = #{username}

SELECT /* bookSelectMapper.findAll */ b.ID , TITLE , AUTHOR , STATUS , MAIN_IMAGE , CREATED_BY , CREATED_AT FROM BOOK b
WHERE LOWER(TITLE) LIKE CONCAT(CONCAT('%', #{searchText}), '%') OR LOWER(AUTHOR) LIKE CONCAT(CONCAT('%', #{searchText}), '%')
ORDER BY ID DESC LIMIT #{jumpCount}, #{selectCount}
