mapper.update:
UPDATE BOOK_HISTORY
   SET REQUESTED_STATUS = #{requestedStatus}
       , STARTED_AT = NOW()
 WHERE BOOK_ID = #{bookId}
   AND REQUESTED_STATUS = 'WAIT_FOR_RESPONSE'

UPDATE BOOK_HISTORY
   SET REQUESTED_STATUS = #{requestedStatus}
       , ENDED_AT = NOW()
 WHERE BOOK_ID = #{bookId}
   AND REQUESTED_STATUS = 'WAIT_FOR_RESPONSE'
