UPDATE /* historyMapper.updateBookHistoryToOnLoan */
       BOOK_HISTORY
   SET REQUESTED_STATUS = #{requestedStatus}
       , STARTED_AT = NOW()
 WHERE ID = #{id}

UPDATE /* historyMapper.updateBookHistoryToAvailable */
       BOOK_HISTORY
   SET REQUESTED_STATUS = #{requestedStatus}
       , ENDED_AT = NOW()
 WHERE ID = #{id}
